import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { Form, Button, Card, Alert, Container } from 'react-bootstrap';

const SignupPage = () => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [numberOfPlants, setNumberOfPlants] = useState('');
    const [companyName, setCompanyName] = useState(''); // <-- new state for company name
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const navigate = useNavigate();

    const BACKEND_URL = 'http://localhost:5000';

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');

        if (username.length < 3) {
            setError('Username must be at least 3 characters long.');
            return;
        }

        if (!numberOfPlants || isNaN(numberOfPlants) || Number(numberOfPlants) <= 0) {
            setError('Please enter a valid number of plants (greater than 0).');
            return;
        }

        if (!companyName.trim()) {
            setError('Please enter your company name.');
            return;
        }

        try {
            const response = await axios.post(`${BACKEND_URL}/api/auth/register`, { 
                username, 
                email, 
                password,
                numberOfPlants: Number(numberOfPlants), // send as number
                companyName: companyName.trim(),        // send trimmed company name
            });
            setSuccess('Registration successful! Redirecting to login...');
            setTimeout(() => {
                navigate('/login');
            }, 2000);
        } catch (err) {
            console.error('Registration error:', err.response || err);
            const backendError = err.response?.data?.error || err.response?.data?.message;
            setError(backendError || 'Registration failed. Please try again.');
        }
    };

    return (
        <Container className="d-flex align-items-center justify-content-center" style={{ minHeight: "80vh" }}>
            <div className="w-100" style={{ maxWidth: "400px" }}>
                <Card>
                    <Card.Body>
                        <h2 className="text-center mb-4">Sign Up</h2>
                        {error && <Alert variant="danger">{error}</Alert>}
                        {success && <Alert variant="success">{success}</Alert>}
                        <Form onSubmit={handleSubmit}>
                            <Form.Group id="username">
                                <Form.Label>Username</Form.Label>
                                <Form.Control
                                    type="text"
                                    value={username}
                                    onChange={(e) => setUsername(e.target.value)}
                                    required
                                />
                            </Form.Group>
                            <Form.Group id="email">
                                <Form.Label className="mt-2">Email</Form.Label>
                                <Form.Control
                                    type="email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    required
                                />
                            </Form.Group>
                            <Form.Group id="password">
                                <Form.Label className="mt-2">Password</Form.Label>
                                <Form.Control
                                    type="password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    required
                                />
                            </Form.Group>

                            <Form.Group id="numberOfPlants">
                                <Form.Label className="mt-2">Number of Plants</Form.Label>
                                <Form.Control
                                    type="number"
                                    min="1"
                                    value={numberOfPlants}
                                    onChange={(e) => setNumberOfPlants(e.target.value)}
                                    required
                                />
                            </Form.Group>

                            {/* New Input for Company Name */}
                            <Form.Group id="companyName">
                                <Form.Label className="mt-2">Company Name</Form.Label>
                                <Form.Control
                                    type="text"
                                    value={companyName}
                                    onChange={(e) => setCompanyName(e.target.value)}
                                    required
                                />
                            </Form.Group>

                            <Button className="w-100 mt-3" type="submit">Sign Up</Button>
                        </Form>
                    </Card.Body>
                </Card>
                <div className="w-100 text-center mt-2">
                    Already have an account? <Link to="/login">Log In</Link>
                </div>
            </div>
        </Container>
    );
};

export default SignupPage;
