import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { Form, Row, Col, Card, Alert, Spinner, Button, InputGroup, ListGroup, Badge } from 'react-bootstrap';
import BarChart from '../components/BarChart';

const initialFormState = {
    carbon_emission: '', water_consumption: '', waste_water: '',
    solid_waste: '', trees_plantation: '', carbon_absorption: ''
};

const units = {
    carbon_emission: 'tCO2e',
    water_consumption: 'KL',
    waste_water: 'KL',
    solid_waste: 'Tonnes',
    trees_plantation: 'Trees',
    carbon_absorption: 'tCO2e'
};

const AdminDashboard = () => {
    const [plants, setPlants] = useState([]);
    const [selectedPlant, setSelectedPlant] = useState('');
    const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
    const [loading, setLoading] = useState(false);
    const [barChartData, setBarChartData] = useState(null);
    const [formData, setFormData] = useState(initialFormState);
    const [message, setMessage] = useState({ type: '', text: '' });
    const [users, setUsers] = useState([]);

    const token = localStorage.getItem('token');

    const authHeader = useMemo(() => ({
        headers: { Authorization: `Bearer ${token}` }
    }), [token]);

    useEffect(() => {
        const fetchInitialData = async () => {
            try {
                const [plantsRes, usersRes] = await Promise.all([
                    axios.get('/api/data/plants', authHeader),
                    axios.get('/api/users', authHeader)
                ]);

                setPlants(plantsRes.data);
                if (plantsRes.data.length > 0) setSelectedPlant(plantsRes.data[0].id);
                
                setUsers(usersRes.data);
            } catch (err) {
                console.error("Failed to fetch initial data", err);
            }
        };
        fetchInitialData();
    }, [authHeader]);

    useEffect(() => {
        if (!selectedPlant || !selectedYear) return;
        
        const fetchData = async () => {
            setLoading(true);
            setMessage({ type: '', text: '' });
            try {
                const res = await axios.get(`/api/data/${selectedPlant}/${selectedYear}`, authHeader);
                const fetchedData = res.data;
                const completeData = Object.keys(initialFormState).reduce((acc, key) => {
                    acc[key] = fetchedData[key] || '';
                    return acc;
                }, {});
                setFormData(completeData);
                
                if (fetchedData && Object.keys(fetchedData).length > 0) {
                    const labels = ['Carbon Emission (tCO2e)', 'Water Consumption (KL)', 'Waste Water (KL)', 'Solid Waste (Tonnes)'];
                    const values = [fetchedData.carbon_emission, fetchedData.water_consumption, fetchedData.waste_water, fetchedData.solid_waste];
                    setBarChartData({
                        labels,
                        datasets: [{ label: `Metrics for ${selectedYear}`, data: values, backgroundColor: '#36A2EB' }]
                    });
                } else {
                    setBarChartData(null);
                }
            } catch (err) {
                console.error("Failed to fetch data", err);
                setFormData(initialFormState);
                setBarChartData(null);
            }
            setLoading(false);
        };
        fetchData();
    }, [selectedPlant, selectedYear, authHeader]);

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const payload = { ...formData, plant_id: selectedPlant, year: selectedYear };
            await axios.post('/api/data', payload, authHeader);
            setMessage({ type: 'success', text: 'Data saved successfully!' });
        } catch (err) {
            setMessage({ type: 'danger', text: 'Failed to save data.' });
        }
        setLoading(false);
    };
    
    const handlePromote = async (userId) => {
        if (window.confirm('Are you sure you want to promote this user to Admin?')) {
            try {
                await axios.put(`/api/users/${userId}/role`, { role: 'admin' }, authHeader);
                setUsers(users.map(user => user.id === userId ? { ...user, role: 'admin' } : user));
            } catch (error) {
                console.error('Failed to promote user', error);
                alert('Failed to promote user.');
            }
        }
    };

    return (
        <div>
            <h2>Admin Dashboard</h2>
            <p>View and manage sustainability data and users.</p>
            <Row>
                <Col lg={5}>
                    <h3>Manage Data</h3>
                    <Card className="p-3">
                         <Form onSubmit={handleSubmit}>
                            <Row>
                                <Col md={6} className="mb-2">
                                    <Form.Label>Plant</Form.Label>
                                    <Form.Select value={selectedPlant} onChange={(e) => setSelectedPlant(e.target.value)} disabled={loading}>
                                        {plants.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}
                                    </Form.Select>
                                </Col>
                                <Col md={6} className="mb-2">
                                    <Form.Label>Year</Form.Label>
                                    <Form.Select value={selectedYear} onChange={(e) => setSelectedYear(e.target.value)} disabled={loading}>
                                        <option value="2022">2022</option>
                                        <option value="2023">2023</option>
                                        <option value="2024">2024</option>
                                        <option value="2025">2025</option>
                                    </Form.Select>
                                </Col>
                            </Row>
                            <hr />
                            {Object.keys(initialFormState).map(key => (
                                <Form.Group key={key} className="mb-2">
                                    <Form.Label>{key.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}</Form.Label>
                                    <InputGroup>
                                        <Form.Control type="number" step="any" name={key} value={formData[key]} onChange={handleInputChange} placeholder="0" required />
                                        <InputGroup.Text>{units[key]}</InputGroup.Text>
                                    </InputGroup>
                                </Form.Group>
                            ))}
                            <Button type="submit" className="w-100 mt-3" disabled={loading || !selectedPlant}>
                                {loading ? <Spinner as="span" animation="border" size="sm" /> : 'Save Data'}
                            </Button>
                            {message.text && <Alert variant={message.type} className="mt-3">{message.text}</Alert>}
                        </Form>
                    </Card>
                </Col>
                <Col lg={7}>
                    <h3>Data Visualization</h3>
                    {loading ? (
                        <div className="text-center mt-5"><Spinner animation="border" /></div>
                    ) : barChartData ? (
                        <Card className="p-3"><BarChart chartData={barChartData} title="Current Environmental Metrics" /></Card>
                    ) : (
                        <Alert variant="info" className="mt-4">No data to display. Select a plant and year, or add new data.</Alert>
                    )}
                    
                    <h3 className="mt-4">User Management</h3>
                    <Card>
                        <ListGroup variant="flush">
                            {users.map(user => (
                                <ListGroup.Item key={user.id} className="d-flex justify-content-between align-items-center">
                                    <div>
                                        <strong>{user.username}</strong>
                                        <br />
                                        <small className="text-muted">{user.email}</small>
                                    </div>
                                    <div>
                                        {user.role === 'admin' ? (
                                            <Badge bg="success">Admin</Badge>
                                        ) : (
                                            <Button variant="outline-primary" size="sm" onClick={() => handlePromote(user.id)}>
                                                Promote to Admin
                                            </Button>
                                        )}
                                    </div>
                                </ListGroup.Item>
                            ))}
                        </ListGroup>
                    </Card>
                </Col>
            </Row>
        </div>
    );
};

export default AdminDashboard;
